mapper.insert:
	/* board.selectBoardInfo */

	INSERT INTO board(
		board_title
		,board_reg_user
		,board_content)
	VALUES(
		 #{board_title}
		,#{board_reg_user}
		,#{board_content}
	)

mapper.update:
	/* board.updateBoardInfo */

	UPDATE board SET
		board_title = #{board_title}
		,board_content = #{board_content}
	WHERE BOARD_ID = #{board_id}

mapper.delete:
/* board.deleteBoardInfo */

DELETE
FROM board
WHERE board_id = #{board_id}
