mapper.select:
/* board.selectBoardInfo */
SELECT board_id boardId ,board_title boardTitle ,convert(char(20),board_date,20) boardDate ,board_reg_user boardRegUser
FROM board

mapper.insert:
	/* board.insertBoardInfo */

	INSERT INTO board(
		board_title 
		,board_reg_user 
		,board_content
		,board_phone)
	VALUES(
		 #{boardTitle}
		,#{boardRegUser}
		,#{boardContent}
		,#{boardPhone}
	)

mapper.update:
	/* board.updateBoardInfo */

	UPDATE board SET
		board_title = #{boardTitle}
		,board_content  = #{boardContent}
		,board_reg_user = #{boardRegUser}
	WHERE BOARD_ID = #{boardId}

mapper.delete:
/* board.deleteBoardInfo */

DELETE
FROM board
WHERE board_id = #{boardId}
