mapper.insert:
	/* board.selectBoardInfo */

	INSERT INTO board(
		board_title 
		,board_reg_user 
		,board_content)
	VALUES(
		 #{boardTitle}
		,#{boardRegUser}
		,#{boardContent}
	)

mapper.update:
	/* board.updateBoardInfo */

	UPDATE board SET
		board_title = #{boardTitle}
		,board_content  = #{boardContent}
		,board_reg_user = #{boardRegUser}
	WHERE BOARD_ID = #{boardId}

mapper.delete:
/* board.deleteBoardInfo */

DELETE
FROM board
WHERE board_id = #{boardId}
